import React from "react";
import { useState } from "react";
import Card from "./Card";

const Cards = () => {
  const [items, setItems] = useState(
    [
      {
        id: 1,
        img: "data:image/png;base64,",
        stat: "",
      },
      {
        id: 1,
        img: "data:image/png;base64,",
        stat: "",
      },
      {
        id: 2,
        img: "https://upload.wikimedia.org/wikipedia/commons/d/d5/CSS3_logo_and_wordmark.svg",
        stat: "",
      },
      {
        id: 2,
        img: "https://upload.wikimedia.org/wikipedia/commons/d/d5/CSS3_logo_and_wordmark.svg",
        stat: "",
      },
      {
        id: 3,
        img: "https://www.google.com/url?sa=i&url=https%3A%2F%2F1000logos.net%2Fjavascript-logo%2F&psig=AOvVaw1noRGANh3HOYKUINXgefm_&ust=1729382179131000&source=images&cd=vfe&opi=89978449&ved=0CBQQjRxqFwoTCID_0OeQmYkDFQAAAAAdAAAAABAE",
        stat: "",
      },
      {
        id: 3,
        img: "https://www.google.com/url?sa=i&url=https%3A%2F%2F1000logos.net%2Fjavascript-logo%2F&psig=AOvVaw1noRGANh3HOYKUINXgefm_&ust=1729382179131000&source=images&cd=vfe&opi=89978449&ved=0CBQQjRxqFwoTCID_0OeQmYkDFQAAAAAdAAAAABAE",
        stat: "",
      },
      {
        id: 4,
        img: "data:image/png;base64,",
        stat: "",
      },
      {
        id: 4,
        img: "data:image/png;base64,",
        stat: "",
      },
      {
        id: 5,
        img: "data:image/png;base64,",
        stat: "",
      },
      {
        id: 5,
        img: "data:image/png;base64,",
        stat: "",
      },
      {
        id: 6,
        img: "data:image/png;base64,",
        stat: "",
      },
      {
        id: 6,
        img: "data:image/png;base64,",
        stat: "",
      },
      {
        id: 7,
        img: "https://upload.wikimedia.org/wikipedia/commons/thumb/c/cf/Angular_full_color_logo.svg/2048px-Angular_full_color_logo.svg.png",
        stat: "",
      },
      {
        id: 7,
        img: "https://upload.wikimedia.org/wikipedia/commons/thumb/c/cf/Angular_full_color_logo.svg/2048px-Angular_full_color_logo.svg.png",
        stat: "",
      },
      {
        id: 8,
        img: "data:image/png;base64,",
        stat: "",
      },
      {
        id: 8,
        img: "data:image/png;base64,",
        stat: "",
      },
    ].sort(() => Math.random() - 0.5)
  );

  const [prev, setPrev] = useState(-1);

  const check = (current) => {
    if (items[current].id === items[prev].id) {
      items[current].stat = "correct";
      items[prev].stat = "correct";
      setItems([...items]);
      setPrev(-1);
    } else {
      items[current].stat = "wrong";
      items[prev].stat = "wrong";
      setItems([...items]);
      setTimeout(() => {
        items[current].stat = "";
        items[prev].stat = "";
        setItems([...items]);
        setPrev(-1);
      }, 1000);
    }
  };

  const handleClick = (id) => {
    // if (
    //   id === prev ||
    //   items[id].stat === "correct" ||
    //   items[id].stat === "active"
    // ) {
    //   return;
    // }
    if (prev === -1) {
      items[id].stat = "active";
      setItems([...items]);
      setPrev(id);
    } else {
      check(id);
    }
  };

  return (
    <div className="container">
      {items.map((item, index) => (
        <Card key={index} item={item} id={index} handleClick={handleClick} />
      ))}
    </div>
  );
};

export default Cards;
